import React from "react";
import axios from "axios";
import selectWhite from "./icons/select-white.png";
import calendar from "./icons/calendar.png";
import dropdown from "./icons/dropdown.png";
import dropup from "./icons/dropup.png";
import selectGray from "./icons/select-gray.png";

class TimeSeriesControl extends React.Component {
    render() {
        const options = this.props.options.map((x) => 
        <li key={x}><button id={x}>{x}</button></li>)
        return (
            <div>
                <div class="time" id='timeseries_bar'>
                    <p class="time-selection">IFCB104</p>
                    <img src={dropdown} className="time-icon" 
                    alt={'Select time series'}></img>
                </div>
                <div class="time-dropdown" id='timeseries_dropdown'>
                    <ul>options</ul>
                </div>
                <p class="time-label" id='timeseries_label'>Time Series</p>
            </div>
        );
    }
}

class YearControl extends React.Component {
    render() {
        return(
            <div>
                <div class="time" id='year_bar'>
                    <p class="time-selection">2021</p>
                    <img src={dropdown} className="time-icon" 
                    alt={'Select year'}></img>
                </div>
                <div class="time-dropdown" id='year_dropdown'></div>
                <p class="time-label" id='year_label'>Year</p>
            </div>
        );
    }
}

class DayControl extends React.Component {
    render() {
        return(
            <div>
                <div class="time" id='day_bar'>
                    <p class="time-selection">June 6th</p>
                    <img src={dropdown} className="time-icon" 
                    alt={'Select day'}></img>
                </div>
                <div class="time-dropdown" id='day_dropdown'></div>
                <p class="time-label" id='day_label'>Day</p>
            </div>
        );
    }
}

class FileControl extends React.Component {
    render() {
        return(
            <div>
                <div class="time" id='file_bar'>
                    <p class="time-selection">T07:22:15Z</p>
                    <img src={dropdown} className="time-icon" 
                    alt={'Select file'}></img>
                </div>
                <div class="time-dropdown" id='file_dropdown'></div>
                <p class="time-label" id='file_label'>Time Series</p>
            </div>
        );
    }
}

class SetControl extends React.Component {
    render() {
        return(
            <div>
                <div class="set" id="set">
                <p class="time-selection">1</p>
                <img src={dropdown} className="time-icon" alt="select time series"></img>
                </div>
                <p class="time-label">Set</p>
            </div>
        );
    }
}

class PlanktonImage extends React.Component {

  render() {
  const url = this.props.nameSpace + this.props.timestamp + '_' + this.props.ifcb + '_' + this.props.targetNum + '.jpg';
      return(
          <img src={url} className="image" alt={this.props.classification} id={this.props.targetNum + '-image'}></img>
      );
  }
}

class Plankton extends React.Component {

  renderImage() {
      return (
      <div>
          <PlanktonImage 
              nameSpace={'http://128.114.25.154:8888/' + this.props.ifcb + '/'}
              timestamp={this.props.timestamp}
              ifcb={this.props.ifcb}
              targetNum={this.props.targetNum}
              classification={this.props.classification}
          />
      </div>
      );
  }

  render() {
      return(
          <button className="plankton-button" onClick={() => this.props.onClick(this.props.id)}>
              <div className="plankton">
                  {this.renderImage()}
                  <div className="id" id={this.props.targetNum}>
                      <p className="id-text" id={this.props.targetNum + '_text'}>{this.props.classification}</p>
                  </div>
              </div>
          </button>
      );
  }
}

class ClassMenu extends React.Component {
  render() {
      const options = this.props.classes.map((x) => 
      <li key={x}><button id={x} onClick={() => this.props.onClick(x)}>{x}</button></li>);
      return(
      <div className="class-menu">
          <ul>{options}</ul>
      </div>
      );
  }
}

class Annotations extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
          classes: [],
          classPicker: 'Unclassified',
          timeSeriesOptions: [],
