  renderPlankton(i) {
      return <Plankton 
              ifcb={this.state.ifcb}
              timestamp={this.state.timestamp}
              ifcb={this.state.ifcb}
              id={i}
              targetNum={this.state.bin[i].number}
              classification={this.state.bin[i].classification}
              onClick={(i) => this.handlePlanktonClick(i)}
          />;
  }

  renderClassMenu() {
    return <ClassMenu 
          classes={this.state.classes}
          onClick={(name) => this.handleMenuClick(name)}
      />;
  }

  render() {
      return(
      <div>
        <div class="time-controls">
            {this.renderTimeSeriesControl()}
            {this.renderYearControl()}
            {this.renderDayControl()}
            {this.renderFileControl()}
